mapper.insert:
INSERT INTO meten_feedback(
content,
suggestionid,
userid
)
VALUES(
#{content},
#{suggestionid},
#{userid}
)

mapper.select:
SELECT u.openid,u.nickname,s.title,s.content,s.img,s.createTime,f.content,f.createTime 
FROM meten_suggestion s 
LEFT JOIN meten_feedback f ON s.id=f.suggestionid 
LEFT JOIN meten_user u ON s.userid=u.id
WHERE f.userid=#{userid}
ORDER BY f.createTime DESC;

SELECT count(1)  
FROM meten_suggestion s 
LEFT JOIN meten_feedback f ON s.id=f.suggestionid 
WHERE 1=1
 AND s.userId = #{userId}
 AND s.title LIKE CONCAT('%', #{title}, '%')
 AND s.content LIKE CONCAT('%', #{content}, '%')
 AND s.createTime $gt;= #{startDate}
 AND s.createTime $lt;= #{endDate}

SELECT u.openid,u.nickname,s.title,s.content,s.img,s.createTime,f.content fcontent,f.createTime fcreateTime
FROM meten_suggestion s 
LEFT JOIN meten_feedback f ON s.id=f.suggestionid 
LEFT JOIN meten_user u ON s.userid=u.id
WHERE 1=1
 AND s.userId = #{userId}
 AND s.title LIKE CONCAT('%', #{title}, '%')
 AND s.content LIKE CONCAT('%', #{content}, '%')
 AND s.createTime $gt;= #{startDate}
 AND s.createTime $lt;= #{endDate}
ORDER BY s.createTime DESC
LIMIT #{startNum}, #{pageSize}
