mapper.update:
UPDATE meilian_suggestion
SET isFeedback = #{isFeedback}
WHERE id= #{id}

mapper.select:
SELECT COUNT(1) FROM meilian_suggestion 
WHERE isFeedback= 0
 AND title LIKE CONCAT('%', #{title}, '%')
 AND content LIKE CONCAT('%', #{content}, '%')
 AND createTime $gt;= #{startDate}
 AND createTime $lt;= #{endDate}

SELECT * FROM meilian_suggestion 
WHERE isFeedback= 0
 AND title LIKE CONCAT('%', #{title}, '%')
 AND content LIKE CONCAT('%', #{content}, '%')
 AND createTime $gt;= #{startDate}
 AND createTime $lt;= #{endDate}
ORDER BY createTime DESC
LIMIT #{startNum}, #{pageSize}

SELECT u.openid,u.nickname,s.title,s.content,s.img,s.createTime,f.content,f.createTime FROM meilian_suggestion s 
LEFT JOIN meilian_feedback f ON s.id=f.suggestionid 
LEFT JOIN meilian_user u ON s.userid=u.id
WHERE f.userid=#{userid}
ORDER BY f.createTime DESC;
